<script>
import Card from 'app/element/Card.svelte'
import Container from 'app/element/Container.svelte'
import { createEventDispatcher } from 'svelte'

const dispatch = createEventDispatcher()

export let initialDailyGoal = ''
export let dailyGoal = ''
export let savingDailyGoal = false
</script>

<Container>
	<Card>
		<div slot="header">
			Edit the daily goal amount.
		</div>
		<div slot="body">
			<label for="daily-goal">
				Daily Goal (ounces)
			</label>
			<input
				type="number"
				id="daily-goal"
				bind:value={dailyGoal}
			>
			<button
				disabled={savingDailyGoal || dailyGoal === initialDailyGoal}
				on:click={() => dispatch('dailyGoal', dailyGoal)}
			>
				Save
			</button>
		</div>
	</Card>

	<br>

	<uiView />

</Container>
