import Container from './Container.svelte'
import getContainerList from 'app/api/get-container-list.js'

export default ({ asr }) => {
	asr.addState({
		name: 'app.configure.container',
		route: '/container',
		template: Container,
		resolve: async (data, parameters) => {
			return {
				containerList: await getContainerList()
			}
		},
		activate({ domApi }) {
			// fetch more data and update
		}
	})
}
